mapper.select:
select
distinct
from customer_info ci left join employee_info ei on ci.phone_staff_id = ei.id left join ( select sum(pay_price) pay_price, max(create_date) recent_create_date, customer_id from order_info group by customer_id)a on ci.id = a.customer_id
and type = #{condition.type,jdbcType=VARCHAR}
and a.pay_price >= #{condition.payPriceS,jdbcType=DECIMAL}
and a.pay_price <= #{condition.payPriceE,jdbcType=DECIMAL}
and a.recent_create_date >= date_format(#{condition.createDateS,jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s')
and a.recent_create_date <= date_format(#{condition.createDateE,jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s')
order by ${orderByClause}

select count(1) from customer_info ci left join ( select sum(pay_price) pay_price, max(create_date) recent_create_date, customer_id from order_info group by customer_id)a on ci.id = a.customer_id
and type = #{condition.type,jdbcType=VARCHAR}
and a.pay_price >= #{condition.payPriceS,jdbcType=DECIMAL}
and a.pay_price <= #{condition.payPriceE,jdbcType=DECIMAL}
and a.recent_create_date >= date_format(#{condition.createDateS,jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s')
and a.recent_create_date <= date_format(#{condition.createDateE,jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s')

SELECT type, season2, season3, season4, ci.name, eip.name phone_staff_name, eir.name receiver_staff_name, business_phone, phone, visit_date, area, update_date, phone2, relation_id, qq, msn, site, id_card, product, transaction_amount FROM `customer_info` ci left join employee_info eip on ci.phone_staff_id = eip.id left join employee_info eir on ci.phone_staff_id = eir.id
and type = #{condition.type,jdbcType=VARCHAR}
and left(phone, length(#{condition.phoneStage,jdbcType=VARCHAR})) = #{condition.phoneStage,jdbcType=VARCHAR}
and recent_visit_date >= date_format(#{condition.visiteDate1,jdbcType=TIMESTAMP}, '%Y-%m-%d 00:00:00')
and recent_visit_date <= date_format(#{condition.visiteDate2,jdbcType=TIMESTAMP}, '%Y-%m-%d 23:59:59')
and recent_import_date >= date_format(#{condition.importDate1,jdbcType=TIMESTAMP}, '%Y-%m-%d 00:00:00')
and recent_import_date <= date_format(#{condition.importDate2,jdbcType=TIMESTAMP}, '%Y-%m-%d 23:59:59')
and recent_export_date >= date_format(#{condition.exportDate1,jdbcType=TIMESTAMP}, '%Y-%m-%d 00:00:00')
and recent_export_date <= date_format(#{condition.exportDate2,jdbcType=TIMESTAMP}, '%Y-%m-%d 23:59:59')
and (select count(1) from reception_info x where x.customer_id = customer_info.id) >= #{condition.backCountS, jdbcType=DECIMAL}
and (select count(1) from reception_info x where x.customer_id = customer_info.id) <= #{condition.backCountE, jdbcType=DECIMAL}
