mapper.select:
select oi.id, oi.order_number, oi.customer_id, oi.phone_staff_id, oi.receiver_staff_id, oi.status, oi.order_type, oi.pay_type, oi.pay_price, oi.actual_price, oi.finance_flag, oi.finance_type, oi.finance_operator_id, oi.finance_date, oi.warehouse_flag, oi.warehouse_operator_id, oi.warehouse_date, oi.culture_flag, oi.culture_operator_id, oi.culture_date, oi.create_date, oi.create_id, oi.update_date, oi.update_id, oi.delete_flag, ei.name as receiver_staff_name, group_concat(odi.good_name, '&', odi.amount) good_name from order_info oi left join order_detail_info odi on oi.id = odi.order_id left join employee_info ei on oi.receiver_staff_id = ei.id
and customer_id = #{customerId, jdbcType=VARCHAR}
and receiver_staff_id = #{receiverStaffId, jdbcType=VARCHAR}
and date_format(oi.create_date, '%Y-%m-%d') = date_format(#{createDate, jdbcType=VARCHAR}, '%Y-%m-%d')
group by oi.id

select count(1) from ( select 1 from order_info oi left join order_detail_info odi on oi.id = odi.order_id left join employee_info ei on oi.receiver_staff_id = ei.id
and oi.customer_id = #{customerId, jdbcType=VARCHAR}
and oi.receiver_staff_id = #{receiverStaffId, jdbcType=VARCHAR}
and date_format(oi.create_date, '%Y-%m-%d') = date_format(#{createDate, jdbcType=VARCHAR}, '%Y-%m-%d')
group by oi.id)a

  	SELECT oi.id, ci.name customer_name, ci.phone customer_phone, group_concat(concat(od.good_name, '(', od.good_id, ')')) good_name, oi.pay_price, oi.order_type, oi.finance_flag,
  		eip.name as phone_staff_name, eir.name as receiver_staff_name, group_concat(concat(od.good_name, '(', od.good_id, ')', '&', od.amount)) goodsQuantity,
  		oi.order_number,oi.finance_date, oi.warehouse_flag
	FROM order_info oi 
	left join order_detail_info od on oi.id = od.order_id
	left join customer_info ci on oi.customer_id = ci.id
	LEFT JOIN employee_info eip on eip.id = oi.phone_staff_id
	LEFT JOIN employee_info eir on eir.id = oi.receiver_staff_id
    
      
         and od.good_name like concat('%', #{condition.goodsName,jdbcType=VARCHAR}, '%')
      
      
         and ci.name like concat('%', #{condition.customerName,jdbcType=VARCHAR}, '%')
      
      
         and oi.order_type = #{condition.orderType,jdbcType=VARCHAR}
      
      
         and oi.finance_date = #{condition.financeDate,jdbcType=TIMESTAMP}
      
      
         and oi.finance_flag = #{condition.financeFlag,jdbcType=TIMESTAMP}
      
      
         and oi.receiver_staff_id = #{condition.userId,jdbcType=TIMESTAMP}
      
      
      
         and (culture_flag = 1 or ((oi.order_type = 2 or oi.order_type = 3) and (warehouse_flag = '' or warehouse_flag = 0)))
      
      
         and oi.id = #{condition.orderId,jdbcType=VARCHAR}
      
      
         and oi.delete_flag = #{condition.deleteFlag,jdbcType=VARCHAR}
      
    
    group by id

  	select count(1) from (
  	SELECT oi.id, ci.name customer_name, ci.phone customer_phone, group_concat(od.good_name) good_name, oi.pay_price, oi.order_type
	FROM order_info oi 
	left join order_detail_info od on oi.id = od.order_id
	left join customer_info ci on oi.customer_id = ci.id
	LEFT JOIN employee_info eip on eip.id = oi.phone_staff_id
	LEFT JOIN employee_info eir on eir.id = oi.receiver_staff_id
    
      
         and od.good_name like concat('%', #{condition.goodsName,jdbcType=VARCHAR}, '%')
      
      
         and ci.name like concat('%', #{condition.customerName,jdbcType=VARCHAR}, '%')
      
      
         and oi.order_type = #{condition.orderType,jdbcType=VARCHAR}
      
      
         and oi.finance_date = #{condition.financeDate,jdbcType=TIMESTAMP}
      
      
         and oi.finance_flag = #{condition.financeFlag,jdbcType=TIMESTAMP}
      
      
         and oi.receiver_staff_id = #{condition.userId,jdbcType=TIMESTAMP}
      
      
      
         and (culture_flag = 1 or ((oi.order_type = 2 or oi.order_type = 3) and (warehouse_flag = '' or warehouse_flag = 0)))
      
      
         and oi.id = #{condition.orderId,jdbcType=VARCHAR}
      
      
         and oi.delete_flag = #{condition.deleteFlag,jdbcType=VARCHAR}
      
    
    ) a
