mapper.update:
update customer_info 
set type = #{condition.type,jdbcType=VARCHAR}
where id =#{condition.cid,jdbcType=VARCHAR};

update customer_info 
set product = CONCAT(ifnull(product,''),#{condition.product,jdbcType=VARCHAR}), 
transaction_amount = ifnull(transaction_amount,0) + #{condition.amount,jdbcType=DECIMAL}
where id =#{condition.cid,jdbcType=VARCHAR};

update customer_info 
set gift = CONCAT(ifnull(gift,''),#{condition.gift,jdbcType=VARCHAR}) 
where id =#{condition.cid,jdbcType=VARCHAR};

mapper.select:
select '成单' as type, (select count(r.customer_id) from reception_info r left join customer_info c on r.customer_id = c.id left join order_info o on c.id = o.customer_id
and c.type = '3' and o.finance_flag = '1'
) as visit_count, count(o.id) as out_order_count, sum(ofs.pay_amount_actual) as out_prices, concat( round( count(o.id)/ (select count(r.customer_id) from reception_info r left join customer_info c on r.customer_id = c.id left join order_info o on c.id = o.customer_id
and c.type = '3' and o.finance_flag = '1'
)*100,2),'%') as out_rate, concat( round( sum(ofs.pay_amount_actual)/( select SUM(of.pay_amount_actual) from order_info oi left join order_fund_settlement of on oi.order_number = of.order_number
and o.finance_flag = '1'
)*100,2),'%') as total_perfor_percentage from customer_info c left join order_info o on c.id = o.customer_id left join order_fund_settlement ofs on o.order_number = ofs.order_number
and c.type = '3' and o.finance_flag = '1'
UNION select '锁定' as type, (select count(r.customer_id) from reception_info r left join customer_info c on r.customer_id = c.id left join order_info o on c.id = o.customer_id
and c.type = '4' and o.finance_flag = '1'
) as visit_count, count(o.id) as out_order_count, sum(ofs.pay_amount_actual) as out_prices, concat( round( count(o.id)/ (select count(r.customer_id) from reception_info r left join customer_info c on r.customer_id = c.id left join order_info o on c.id = o.customer_id
and c.type = '4' and o.finance_flag = '1'
)*100,2),'%') as out_rate, concat( round( sum(ofs.pay_amount_actual)/( select SUM(of.pay_amount_actual) from order_info oi left join order_fund_settlement of on oi.order_number = of.order_number
and o.finance_flag = '1'
)*100,2),'%') as total_perfor_percentage from customer_info c left join order_info o on c.id = o.customer_id left join order_fund_settlement ofs on o.order_number = ofs.order_number
and c.type = '4' and o.finance_flag = '1'
UNION select '说明会' as type, (select count(r.customer_id) from reception_info r left join customer_info c on r.customer_id = c.id left join order_info o on c.id = o.customer_id
and c.type = '2' and o.finance_flag = '1'
) as visit_count, count(o.id) as out_order_count, sum(ofs.pay_amount_actual) as out_prices, concat( round( count(o.id)/ (select count(r.customer_id) from reception_info r left join customer_info c on r.customer_id = c.id left join order_info o on c.id = o.customer_id
and c.type = '2' and o.finance_flag = '1'
)*100,2),'%') as out_rate, concat( round( sum(ofs.pay_amount_actual)/( select SUM(of.pay_amount_actual) from order_info oi left join order_fund_settlement of on oi.order_number = of.order_number
and o.finance_flag = '1'
)*100,2),'%') as total_perfor_percentage from customer_info c left join order_info o on c.id = o.customer_id left join order_fund_settlement ofs on o.order_number = ofs.order_number
and c.type = '2' and o.finance_flag = '1'
UNION select '重复登门' as type, (select count(r.customer_id) from reception_info r left join customer_info c on r.customer_id = c.id left join order_info o on c.id = o.customer_id
and c.type = '1' and o.finance_flag = '1'
) as visit_count, count(o.id) as out_order_count, sum(ofs.pay_amount_actual) as out_prices, concat( round( count(o.id)/ (select count(r.customer_id) from reception_info r left join customer_info c on r.customer_id = c.id left join order_info o on c.id = o.customer_id
and c.type = '1' and o.finance_flag = '1'
)*100,2),'%') as out_rate, concat( round( sum(ofs.pay_amount_actual)/( select SUM(of.pay_amount_actual) from order_info oi left join order_fund_settlement of on oi.order_number = of.order_number
and o.finance_flag = '1'
)*100,2),'%') as total_perfor_percentage from customer_info c left join order_info o on c.id = o.customer_id left join order_fund_settlement ofs on o.order_number = ofs.order_number
and c.type = '1' and o.finance_flag = '1'
UNION select '转介绍' as type, (select count(r.customer_id) from reception_info r left join customer_info c on r.customer_id = c.id left join order_info o on c.id = o.customer_id
and c.type = '5' and o.finance_flag = '1'
) as visit_count, count(o.id) as out_order_count, sum(ofs.pay_amount_actual) as out_prices, concat( round( count(o.id)/ (select count(r.customer_id) from reception_info r left join customer_info c on r.customer_id = c.id left join order_info o on c.id = o.customer_id
and c.type = '5' and o.finance_flag = '1'
)*100,2),'%') as out_rate, concat( round( sum(ofs.pay_amount_actual)/( select SUM(of.pay_amount_actual) from order_info oi left join order_fund_settlement of on oi.order_number = of.order_number
and o.finance_flag = '1'
)*100,2),'%') as total_perfor_percentage from customer_info c left join order_info o on c.id = o.customer_id left join order_fund_settlement ofs on o.order_number = ofs.order_number
and c.type = '5' and o.finance_flag = '1'
