mapper.select:
  	select 
		e.id as employee_id,
		(
		select sum(of.pay_amount_actual) 
		from order_info o
		left join order_fund_settlement of on o.order_number = of.order_number 
		where e.id = o.receiver_staff_id 
		
	     and o.create_date >= #{condition.startTime,jdbcType=TIMESTAMP}
	     and o.create_date <= #{condition.endTime,jdbcType=TIMESTAMP}
	    
		) as performance,
		(
		select sum(od.amount) 
		from order_detail_info od 
		where od.order_id in (
			select o.id from order_info o 
			where e.id = o.receiver_staff_id 
			
		     and o.create_date >= #{condition.startTime,jdbcType=TIMESTAMP}
		     and o.create_date <= #{condition.endTime,jdbcType=TIMESTAMP}
		    )
		) as order_amounts,
		getNewCustomerCounts(e.id, #{condition.startTime}, #{condition.endTime}) as new_customer_counts
	from employee_info e
