mapper.select:
select TIMESTAMPDIFF(MINUTE, r.start_time, r.end_time) as total_time, date_format(r.start_time, '%h:%i') as start_time, date_format(r.create_date, '%Y-%m-%d') as create_date, c.id as customer_id, c.`name` as customer_name, c.phone as phone, (select e.`name` from employee_info e where e.id = r.phone_staff_id) as phone_staff_name, (select e.`name` from employee_info e where e.id = r.receiver_staff_id) as receiver_staff_name, c.type as type, (select ci.`name` from customer_info ci where ci.id = c.relation_id) as relation_name, c.birthday as birthday, c.area as area, c.vip_card as vip_card, date_format(r.end_time, '%h:%i') as end_time, (select count(o.id) from order_info o where o.customer_id = c.id
and o.create_date >= #{condition.startDate,jdbcType=TIMESTAMP}
and o.create_date <= #{condition.endDate,jdbcType=TIMESTAMP}
) AS order_count from reception_info r left join customer_info c on r.customer_id = c.id
order by ${orderByClause}

mapper.update:
update reception_info
set end_time = now() 
where customer_id = #{condition.customerId,jdbcType=VARCHAR}
and end_time is null
order by create_date Desc limit 1 ;

update reception_info
set order_id = #{condition.orderId,jdbcType=VARCHAR}
where customer_id = #{condition.customerId,jdbcType=VARCHAR}
and end_time is null
order by create_date Desc limit 1 ;

update reception_info
set present_order_id = #{condition.presentOrderId,jdbcType=VARCHAR},present_name = #{condition.presentName,jdbcType=VARCHAR}
where customer_id = #{condition.customerId,jdbcType=VARCHAR}
and end_time is null
order by create_date Desc limit 1 ;
