mapper.select:
SELECT receiver_staff_id, receiver_staff_name, IFNULL(sum(locked_counts),0) as locked_counts, IFNULL(sum(finish_order_counts),0) as finish_order_counts, IFNULL(sum(roadshow_counts),0) as roadshow_counts, IFNULL(sum(repeat_counts),0) as repeat_counts, IFNULL(sum(new_counts),0) as new_counts from visit_report_info
group by receiver_staff_id
order by ${orderByClause}

select e.id as receiver_staff_id, e.`name` as receiver_staff_name, IFNULL(sum(v.locked_counts),0) as locked_counts, IFNULL(sum(v.locked_orders),0) as locked_orders, IFNULL(sum(v.locked_amounts),0) as locked_amounts, IFNULL(sum(v.finish_order_counts),0) as finish_order_counts, IFNULL(sum(v.finish_orders),0) as finish_orders, IFNULL(sum(v.finish_amounts),0) as finish_amounts, IFNULL(sum(v.repeat_counts),0) as repeat_counts, IFNULL(sum(v.repeat_orders),0) as repeat_orders, IFNULL(sum(v.repeat_amounts),0) as repeat_amounts, IFNULL(sum(v.roadshow_counts),0) as roadshow_counts, IFNULL(sum(v.roadshow_orders),0) as roadshow_orders, IFNULL(sum(v.roadshow_amounts),0) as roadshow_amounts, IFNULL(sum(v.new_counts),0) as new_counts, IFNULL(sum(v.new_orders),0) as new_orders, IFNULL(sum(v.new_amounts),0) as new_amounts, (IFNULL(sum(v.locked_counts),0) + IFNULL(sum(v.finish_order_counts),0) + IFNULL(sum(v.repeat_counts),0) + IFNULL(sum(v.roadshow_counts),0) + IFNULL(sum(v.new_counts),0)) as total_counts, (IFNULL(sum(v.locked_orders),0) + IFNULL(sum(v.finish_orders),0) + IFNULL(sum(v.repeat_orders),0) + IFNULL(sum(v.roadshow_orders),0) + IFNULL(sum(v.new_orders),0)) as total_orders, (IFNULL(sum(v.locked_amounts),0) + IFNULL(sum(v.finish_amounts),0) + IFNULL(sum(v.repeat_amounts),0) + IFNULL(sum(v.roadshow_amounts),0) + IFNULL(sum(v.new_amounts),0)) as total_amounts from employee_info e left join visit_report_info v on e.id = v.receiver_staff_id
group by e.`name`
order by ${orderByClause}

  	select count(1) from (
  		select o.customer_id from order_info o 
  		where o.customer_id = #{condition.customerId,jdbcType=VARCHAR}
        
	        and o.create_date >= #{condition.startTime,jdbcType=TIMESTAMP}
	        and o.create_date <= #{condition.endTime,jdbcType=TIMESTAMP}
        
  		and (o.`status` = '4' or o.pay_type = '1')
  		group by  o.customer_id
  		having count(*) = 1
  	) a
