mapper.select:
select COUNT(1) achieveCount, SUM(pay_price) payPrice, SUM(actual_price) actualPrice, group_concat(distinct ei.name) phoneStaffName from order_info oi left join employee_info ei on oi.phone_staff_id = ei.id
and receiver_staff_id = #{userId}
and receiver_staff_id in
#{item}
and DATE_FORMAT(oi.create_date, '%Y-%m-%d') = #{dateInfo}
GROUP BY receiver_staff_id

select
count(1) receptionCount,
FORMAT((TIME_TO_SEC(end_time) - TIME_TO_SEC(start_time))/60,1) stateTime
from reception_info

	
		and receiver_staff_id = #{userId}
	
	
		and receiver_staff_id in
		
			#{item}
		
	
	
		and DATE_FORMAT(create_date, '%Y-%m-%d') = #{dateInfo}
	

GROUP BY receiver_staff_id

SELECT ci.name as customerName,ci.phone as customerPhone, ri.start_time as startTime, ri.end_time as endTime, ifnull(ri.order_id,' ') as orderNumber FROM `reception_info` ri left join customer_info ci on ri.customer_id = ci.id
and ri.receiver_staff_id = #{receiverStaffId}
and DATE_FORMAT(ri.create_date, '%Y-%m-%d') = DATE_FORMAT(#{createDate}, '%Y-%m-%d')

SELECT count(1) FROM `reception_info` ri left join customer_info ci on ri.customer_id = ci.id
and ri.receiver_staff_id = #{receiverStaffId}
and DATE_FORMAT(ri.create_date, '%Y-%m-%d') = DATE_FORMAT(#{createDate}, '%Y-%m-%d')

select ei.id as employeeId, ei.name as employeeName, count(oi.id) orderCount, 
	sum(pay_price) payPrice, sum(actual_price) actualPrice, count(distinct ri.id) receptionCount
from employee_info ei
left join reception_info ri on ri.receiver_staff_id = ei.id
left join order_info oi on ei.id = oi.receiver_staff_id

	
		and ei.id in
		
			#{item}
		
	
	
		and DATE_FORMAT(oi.create_date, '%Y-%m-%d') = #{dateInfo}
		and DATE_FORMAT(ri.create_date, '%Y-%m-%d') = #{dateInfo}
	

group by ei.id
