mapper.select:
select COUNT(1) achieveCount, SUM(pay_price) payPrice, SUM(actual_price) actualPrice, group_concat(distinct ei.name) phoneStaffName from order_info oi left join employee_info ei on oi.phone_staff_id = ei.id
and receiver_staff_id = #{userId}
and receiver_staff_id in
#{item}
and DATE_FORMAT(oi.create_date, '%Y-%m-%d') = #{dateInfo}
GROUP BY receiver_staff_id

select
count(1) receptionCount,
FORMAT((TIME_TO_SEC(end_time) - TIME_TO_SEC(start_time))/60,1) stateTime
from reception_info

	
		and receiver_staff_id = #{userId}
	
	
		and receiver_staff_id in
		
			#{item}
		
	
	
		and DATE_FORMAT(create_date, '%Y-%m-%d') = #{dateInfo}
	


	GROUP BY receiver_staff_id

select case when ci.type = 0 then '空白客户' when ci.type = 1 then '重复登门' when ci.type = 2 then '说明会' when ci.type = 3 then '成单' when ci.type = 4 then '锁定' when ci.type = 5 then '转介绍' end as typeName ,count(ci.type) typeCount from reception_info ri left join customer_info ci on ri.customer_id = ci.id
and ri.receiver_staff_id = #{userId}
and ri.receiver_staff_id in
#{item}
and DATE_FORMAT(ri.create_date, '%Y-%m-%d') = #{dateInfo}
GROUP BY ci.type

SELECT ci.name as customerName,REPLACE(ci.phone, SUBSTR(ci.phone,4,4), '****') as customerPhone, ri.start_time as startTime, ri.end_time as endTime, ifnull(ri.order_id,' ') as orderNumber, case when ci.type = 0 then '空白客户' when ci.type = 1 then '重复登门' when ci.type = 2 then '说明会' when ci.type = 3 then '成单' when ci.type = 4 then '锁定' when ci.type = 5 then '转介绍' end as typeName FROM `reception_info` ri left join customer_info ci on ri.customer_id = ci.id
and ri.receiver_staff_id = #{receiverStaffId}
and DATE_FORMAT(ri.create_date, '%Y-%m-%d') = DATE_FORMAT(#{createDate}, '%Y-%m-%d')

SELECT count(1) FROM `reception_info` ri left join customer_info ci on ri.customer_id = ci.id
and ri.receiver_staff_id = #{receiverStaffId}
and DATE_FORMAT(ri.create_date, '%Y-%m-%d') = DATE_FORMAT(#{createDate}, '%Y-%m-%d')

select ei.id as employeeId, ei.name as employeeName, count(distinct oi.id) orderCount, ifnull(sum(distinct pay_price),0) payPrice, ifnull(sum( distinct actual_price),0) actualPrice, count(distinct ri.id) receptionCount from employee_info ei left join reception_info ri on ri.receiver_staff_id = ei.id
and DATE_FORMAT(ri.create_date, '%Y-%m-%d') = #{dateInfo}
left join order_info oi on ei.id = oi.receiver_staff_id
and DATE_FORMAT(oi.create_date, '%Y-%m-%d') = #{dateInfo}
and ei.id in
#{item}
and ei.id in (select employee_id from daily_employee_audit where auditor_id = ${auditor} and delete_flag = 0 and status in (0, 1) and DATE_FORMAT(create_date, '%Y-%m-%d') = #{dateInfo})
group by ei.id

select oi.id, oi.order_number, oi.customer_id, oi.phone_staff_id, oi.receiver_staff_id, oi.status, case when oi.order_type = 1 then '正常' when oi.order_type = 2 then '退货' when oi.order_type = 3 then '换货' when oi.order_type = 4 then '赠品' else ' ' end as orderTypeName, case when oi.pay_type = 0 then '全额支付' when oi.pay_type = 1 then '订金支付' when oi.pay_type = 2 then '派送支付' else ' ' end as payTypeName, oi.pay_price, oi.actual_price, oi.finance_flag,oi.finance_type, oi.finance_operator_id, oi.finance_date, oi.warehouse_flag, oi.warehouse_operator_id, oi.warehouse_date, case when oi.culture_flag = 1 then '已审核' when oi.culture_flag = -1 then '未通过' else '未审核' end as culture_flag, oi.culture_operator_id, oi.culture_date, oi.Remarks, oi.create_date, oi.create_id, oi.update_date, oi.update_id, oi.delete_flag, (select count(1) from order_detail_info a where a.order_id = oi.id and a.good_type=2) as saleCount, (select count(1) from order_detail_info a where a.order_id = oi.id and a.good_type=3) as freeCount from order_info oi inner join order_detail_info odi on oi.id = odi.order_id where odi.good_type in (2,3) and oi.finance_flag = 1
and oi.order_number = #{orderNumber}
group by oi.id order by culture_flag asc, oi.create_date desc
